import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import { Line } from 'react-chartjs-2';

const socket = io('http://localhost:4000');

function App() {
  const [timestamps, setTimestamps] = useState([]);
  const [roomTP, setRoomTP] = useState([]);
  const [roomLuminosity, setRoomLuminosity] = useState([]);
  const [roomNoise, setRoomNoise] = useState([]);

  useEffect(() => {
    socket.on('connect', () => {
      console.log('Connected to WebSocket server');
    });

    socket.on('data', (newData) => {
      console.log('Received data:', newData);
      setTimestamps((prev) => [...prev, newData.timestamp]);
      setRoomTP((prev) => [...prev, newData.roomTP]);
      setRoomLuminosity((prev) => [...prev, newData.roomLuminosity]);
      setRoomNoise((prev) => [...prev, newData.roomNoise]);
    });

    socket.on('disconnect', () => {
      console.log('Disconnected from WebSocket server');
    });

    return () => {
      socket.off('data');
      socket.off('connect');
      socket.off('disconnect');
    };
  }, []);

  const roomTPData = {
    labels: timestamps,
    datasets: [
      {
        label: 'Room Temperature (Celsius)',
        data: roomTP,
        fill: false,
        backgroundColor: 'rgba(75,192,192,0.2)',
        borderColor: 'rgba(75,192,192,1)',
      },
    ],
  };

  const roomLuminosityData = {
    labels: timestamps,
    datasets: [
      {
        label: 'Room Luminosity (Lux)',
        data: roomLuminosity,
        fill: false,
        backgroundColor: 'rgba(255,206,86,0.2)',
        borderColor: 'rgba(255,206,86,1)',
      },
    ],
  };

  const roomNoiseData = {
    labels: timestamps,
    datasets: [
      {
        label: 'Room Noise (dB)',
        data: roomNoise,
        fill: false,
        backgroundColor: 'rgba(153,102,255,0.2)',
        borderColor: 'rgba(153,102,255,1)',
      },
    ],
  };

  return (
    <div className="App">
      <h1>Real-Time CoAP Data Plot</h1>
      <div>
        <h2>Room Temperature</h2>
        <Line data={roomTPData} />
      </div>
      <div>
        <h2>Room Luminosity</h2>
        <Line data={roomLuminosityData} />
      </div>
      <div>
        <h2>Room Noise</h2>
        <Line data={roomNoiseData} />
      </div>
    </div>
  );
}

export default App;
